export function getMicrosoftToken() {
  return request({
    url: "/resource/sharepoint/client/token",
    method: "get"
  })
}

export function getMicrosoftSite() {
  return request({
    url: "/resource/sharepoint/client/main-site",
    method: "get"
  })
}

